    .Call('_TAPE_getScore', PACKAGE = 'TAPE', n_nomissing, q, Y, X, sparsekin, densekin, W, tau, fixtau, is_AI, tol_pcg, maxiter_pcg, nrun_trace, cutoff_trace)
